import React from 'react';
import {useEffect,useState} from 'react';
import './View.css';
import { useParams } from 'react-router-dom';
import { AddCartContext } from '../../Store/AddtoCartContext';
import { useContext } from 'react';
function View() {
  
 const [product,setProduct] = useState({});
 const params = useParams();
 const setProductDetails = useContext(AddCartContext);
  useEffect(() => {
    console.log("params", params.productId.substring(1,2));
    fetch("https://fakestoreapi.com/products/"+ params.productId.substring(1,2)).then((productResponse) => {
      productResponse.json().then((data) => {
        setProduct(data);
        console.log(data);
      });
    });
   
  }, []);
  return (
    <div className="viewParentDiv">
      <div className="imageShowDiv">
        <img
          src={product.image}
          alt=""
        />
      </div>
      <div className="rightSection">
        <div className="productDetails">
          <p>{'$ '+product.price}</p>
          <span>{product.title}</span>
            <p>{product.category}</p>
          <span>{product.description}</span>
        </div>
        <div className="addCart" onClick={()=>{
          setProductDetails([...productDetails,{product}])
        }}>
          <p className='cartBtn'>ADD TO CART</p>
        </div>
      </div>
    </div>
  );
}
export default View;
