import React from 'react';
import {useContext} from 'react';
import './View.css';
import { PostContext } from '../../Store/PostContext';
import { AddCartContext } from '../../Store/AddtoCartContext';

function View() {
  const {postDetails} = useContext(PostContext)
  const {setProductDetails} = useContext(AddCartContext);
  return (
    <div className="viewParentDiv">
      <div className="imageShowDiv">
        <img
          src={postDetails.image}
          alt=""
        />
      </div>
      <div className="rightSection">
        <div className="productDetails">
          <p>{'$ '+postDetails.price}</p>
          <span>{postDetails.title}</span>
            <p>{postDetails.category}</p>
          <span>{postDetails.description}</span>
        </div>
        <div className="contactDetails" onClick={
          ()=>{
            setProductDetails=postDetails;
          }
        }>
          <p>ADD TO CART</p>
        </div>
      </div>
    </div>
  );
}
export default View;
