import React from 'react';
import {useContext,useEffect,useState} from 'react';
import './View.css';
import { PostContext } from '../../Store/PostContext';
import { AddCartContext } from '../../Store/AddtoCartContext';
function View() {
  
 const [product,setProduct] = useState({});
 const {postDetails} = useContext(PostContext);
 const [postId,setPostId] = useState('');
 
  useEffect(() => {
    setPostId(postDetails);
    console.log("postdetails",postDetails,"postId",postId);
    fetch("https://fakestoreapi.com/products/"+ postDetails).then((productResponse) => {
      productResponse.json().then((data) => {
        setProduct(data);
        console.log(data);
      });
    });
   
  }, []);
  return (
    <div className="viewParentDiv">
      <div className="imageShowDiv">
        <img
          src={product.image}
          alt=""
        />
      </div>
      <div className="rightSection">
        <div className="productDetails">
          <p>{'$ '+product.price}</p>
          <span>{product.title}</span>
            <p>{product.category}</p>
          <span>{product.description}</span>
        </div>
        <div className="contactDetails">
          <p className='cartBtn'>ADD TO CART</p>
        </div>
      </div>
    </div>
  );
}
export default View;
