import { createContext, useState,useEffect } from "react";

export const AddCartContext = createContext(null);


function CartProvider ({children}) {

const [productDetails,setProductDetails] = useState(n)

const addToCart = (item) => {
    console.log("addtocart entry")
    const isItemInCart = productDetails.find((cartItem) => cartItem.id === item.id); // check if the item is already in the cart

    if (isItemInCart) {
      setProductDetails(
        productDetails.map(
          (
            cartItem // if the item is already in the cart, increase the quantity of the item
          ) =>
            cartItem.id === item.id
              ? { ...cartItem, quantity: cartItem.quantity + 1 }
              : cartItem // otherwise, return the cart item
        )
      );
    //   localStorage.setItem("cartItems", JSON.stringify(productDetails));
    //   console.log('storage cart items:',productDetails)
    } else {
      setProductDetails([...productDetails, { ...item, quantity: 1 }]); // if the item is not in the cart, add the item to the cart
    }
    
  };

//   useEffect(() => {
//     console.log('storage 2 cart items:',localStorage.getItem("cartItems"))
//   }, [productDetails]);

//   useEffect(() => {
//     const cartItems = localStorage.getItem("cartItems");
//     console.log('cart items:',cartItems)
//     if (cartItems) {
//         setProductDetails(JSON.parse(cartItems));
//     }
//   }, []);
// [productDetails, setProductDetails] = useState(
//         localStorage.getItem("cartItems")
//           ? JSON.parse(localStorage.getItem("cartItems"))
//           : []
//       );
    return (
        <AddCartContext.Provider value={{productDetails,addToCart}}>
            {children}
        </AddCartContext.Provider>
    )
}
export default CartProvider;
